        content: counter(section) "." counter(subsection);
        margin-right: 10px;
        font-weight: bold;
    }
/* Inner List */
ol.custom-counter .sub-counter .sub-sub-counter {
    list-style-type: none;
    counter-reset: subsubsection;
    padding-left: 20px;
}

    ol.custom-counter .sub-counter .sub-sub-counter > li {
        counter-increment: subsubsection;
        margin-bottom: 10px;
    }

    ol.custom-counter .sub-counter .sub-sub-counter > li::before {
        content: counter(section) "." counter(subsection) "." counter(subsubsection);
        margin-right: 10px;
        font-weight: bold;
    }
/* Sub-Items as Letters */
ol.custom-counter .alpha-counter {
    list-style-type: none;
    counter-reset: subsubsection;
    padding-left: 20px;
}

    ol.custom-counter .alpha-counter > li {
        counter-increment: subsubsection;
        margin-bottom: 10px;
    }

    ol.custom-counter .alpha-counter > li::before {
        content: "("counter(subsubsection, lower-alpha) ")";
        margin-right: 10px;
