if doc.job_opening:
    job_opening_doc = frappe.get_doc('Job Opening', doc.job_opening)
    job_opening_link = frappe.utils.get_link_to_form('Job Opening', doc.job_opening)
    if job_opening_doc.status == 'Closed':
        frappe.throw(f'Can not create Interview against closed Job Opening: {job_opening_link}')

if doc.custom_job_opening:
    job_opening_doc = frappe.get_doc('Job Opening', doc.custom_job_opening)
    job_opening_link = frappe.utils.get_link_to_form('Job Opening', doc.custom_job_opening)
    if job_opening_doc.status == 'Closed':
        frappe.throw(f'Can not create Job Offer against closed Job Opening: {job_opening_link}')

effective_from_date = doc.effective_from
effective_to_date = doc.effective_to
current_date = frappe.utils.getdate(frappe.utils.nowdate())


#Fetch all the leave allocations of employee for this leave policy
leave_allocation = frappe.db.get_all('Leave Allocation',
    filters={
        'employee': doc.employee,
        'leave_policy_assignment': doc.name
    },
    fields=['name', 'leave_type']
)

# Fetch the leave policy document
leave_policy_doc = frappe.get_doc('Leave Policy', doc.leave_policy)

if leave_policy_doc:
    leave_policy_child = leave_policy_doc.get("leave_policy_details")

    for leave in leave_policy_child:
        leave_type = leave.leave_type
        annual_allocation = leave.annual_allocation

        leave_type_doc = frappe.get_doc('Leave Type', leave_type)

        is_sick_leave = leave_type_doc.get("custom_is_sick_leave")
        frequency = leave_type_doc.get("earned_leave_frequency")
        allocate_on_day = leave_type_doc.get("allocate_on_day")
        monthly_allocation = float(leave_type_doc.get("custom_monthly_allocation_for_sick_leave", 0))  # Ensure it's float
        last_month_allocation = leave_type_doc.get("custom_monthly_allocation_for_last_month")

        if effective_from_date and effective_to_date and is_sick_leave == 1 and frequency == "Monthly":
            # Calculate the total number of months
            start_date = frappe.utils.getdate(effective_from_date)
            end_date = frappe.utils.getdate(effective_to_date)
            total_months = (end_date.year - start_date.year) * 12 + (end_date.month - start_date.month) + 1

            # frappe.msgprint(f"Total Months: {total_months}")


            past_month_leave = 0
            past_months = 0 


            last_day_of_current_month = frappe.utils.get_last_day(current_date)

            # Check if the current month is the last month in the given date range
            is_last_month = (current_date.year == end_date.year and current_date.month == end_date.month)


            if is_last_month==True:
                if allocate_on_day == 'First Day':
                    first_day_of_last_month = frappe.utils.get_first_day(last_day_of_current_month.replace(month=current_date.month))
                    if current_date >= first_day_of_last_month:
                        past_months = (current_date.year - start_date.year) * 12 + (current_date.month - start_date.month)
                        past_month_leave = (past_months * monthly_allocation) + last_month_allocation


                elif allocate_on_day == 'Last Day': 
                    last_day_of_last_month = frappe.utils.get_last_day(last_day_of_current_month.replace(month=current_date.month))
                    if current_date >= last_day_of_last_month:
                        past_months = (current_date.year - start_date.year) * 12 + (current_date.month - start_date.month)
                        past_month_leave = (past_months * monthly_allocation) + last_month_allocation
                    else:
                        if current_date < last_day_of_current_month:
                            past_months = (current_date.year - start_date.year) * 12 + (current_date.month - start_date.month)
                        else:
                            past_months = (current_date.year - start_date.year) * 12 + (current_date.month - start_date.month) + 1

                        past_month_leave = past_months * monthly_allocation



            else:
                if allocate_on_day == 'First Day':
                    if current_date >= start_date:
                        past_months = (current_date.year - start_date.year) * 12 + (current_date.month - start_date.month) + 1
                        past_month_leave = past_months * monthly_allocation


                elif allocate_on_day == 'Last Day':
                    if current_date > start_date:

                        last_day_of_current_month = frappe.utils.get_last_day(current_date)

                        if current_date < last_day_of_current_month:
                            past_months = (current_date.year - start_date.year) * 12 + (current_date.month - start_date.month)
                        else:
                            past_months = (current_date.year - start_date.year) * 12 + (current_date.month - start_date.month) + 1

                        past_month_leave = past_months * monthly_allocation


            # frappe.msgprint(f"Past Months: {past_months}, Monthly Allocation: {monthly_allocation}, Past Month Leave: {past_month_leave}")


            if leave_allocation:
                for leave_all in leave_allocation:
                    leave_all_type = leave_all.leave_type
                    leave_all_type_doc = frappe.get_doc('Leave Type', leave_all_type)
                    if leave_all_type_doc.get('custom_is_sick_leave')==1:
                        frappe.db.set_value('Leave Allocation', leave_all.name, 'new_leaves_allocated', past_month_leave)
                        frappe.db.set_value('Leave Allocation', leave_all.name, 'total_leaves_allocated', past_month_leave)

                        leave_ledger = frappe.db.get_value('Leave Ledger Entry', {'transaction_name': leave_all.name}, ['name'])
                        if leave_ledger:
                            frappe.db.set_value('Leave Ledger Entry', leave_ledger, 'leaves', past_month_leave)
