css:
.print-format{
    padding: 0.40in !important;
    font-size: 14px !important;
}

@media print {
    @page {
        margin: 0in !important;
    }
}
ol.custom-counter {
        list-style-type: none;
        counter-reset: section;
}
    ol.custom-counter > li {
        counter-increment: section;
        margin-bottom: 10px;
    }
    ol.custom-counter > li::before {
        content: counter(section) ")";
        margin-right: 10px;
    }
ol.custom-counteralpha {
    list-style-type: none;
    counter-reset: section;
}

    ol.custom-counteralpha > li {
        counter-increment: section;
        margin-bottom: 10px;
    }

    ol.custom-counteralpha > li::before {
        content: counter(section, lower-alpha) ")";
        margin-right: 10px;
    }
.sectiontable{
    margin: 0.5rem 8rem 2rem 0rem!important;
}
td{
    height: 80% !important;
}
td:first-child{
    width: 60%;
}
td:nth-child(2){
    width: 40%;
    text-align: center;
}
td{
    color: #000 !important;
    padding: 0px !important;
}
.print-format td, .print-format td {
    padding: 5px !important;
}
.center-align {
    text-align: center;
}
.left-align {
    text-align: left;
}
.fixed-height {
        height: 25px;
    }
.total{
    background-color: #f4f5f6;
}
.head{
    font-size: 12px !important;
}
