css:
.print-format{
    padding: 0.20in !important;
    font-size: 14px !important;
}

@media print {
    @page {
        margin: 0in !important;
    }
} 

.print-format td, .print-format th {
    padding: 7PX 5px !important;
    font-size: 12px !important;
}

.column {
  float: left;
  width: 50%;
}

/* Clear floats after the columns */
.row:after {
  content: "";
  display: table;
  clear: both;
}
.row {
    margin-right: 0px !important;
     margin-left: 0px !important; 
}
table {
    border-collapse: collapse;
    width: 100%;
    margin: 20px 0px !important;
}

th, td {
    border: 1px solid black;
    padding: 8px;
    text-align: left;
    color: #000000 !important;
}

th {
    color: #000000 !important;
    font-weight: bold !important;
}

.custom-checkbox {
    display: inline-flex;
    align-items: center; 
    font-size: 16px;
    vertical-align: middle;
    margin: 0px 15px !important;
}

.custom-checkbox input {
    position: absolute;
    opacity: 0;
    height: 0;
    width: 0;
}


.custom-checkbox .checkmark {
    display: inline-block;
    position: relative;
    height: 15px; /* Adjust size to 14px */
    width: 15px;  /* Adjust size to 14px */
    border: 1px solid #808080;
    border-radius: 4px;
    margin: 0px 5px 0px 0px;
    top: 2px;
    text-align: center;
    line-height: 14px; /* Center checkmark vertically */
    color: transparent; /* Default color */
}

.custom-checkbox input:checked ~ .checkmark {
    background-color: #2490EF; /* Background color when checked */
    border: 1px solid #2490EF; /* Border color when checked */
    color: transparent; /* Ensure no default color for checkmark */
}

/* Checkmark appearance */
.custom-checkbox input:checked ~ .checkmark:after {
    content: "\2714"; /* Unicode checkmark character */
    position: absolute;
    right: 1px;
    font-size: 13px; /* Adjust size of the checkmark */
    color: white; /* Color of the checkmark */
}
